USE ROLE SYSADMIN;
USE DATABASE MY_DATABASE;
USE SCHEMA PUBLIC;
USE WAREHOUSE COMPUTE_WH;

INSERT INTO MY_DATABASE.PUBLIC.DEMO_USERS_TRANSFORMED(USER_ID, RESULT) 
(SELECT du1.USER_ID, my_py(du1.USER_ID, du1.ZIP_CODE) FROM US_ZIP_CODE_CROSSWALK_HUDUSPS.ZIPCODE_CROSSWALK.DEMO_USERS AS du1 WHERE du1.USER_ID NOT IN 
(SELECT du2.USER_ID FROM MY_DATABASE.PUBLIC.DEMO_USERS_TRANSFORMED AS du2) LIMIT 3);
